mapper.select:
SELECT M.me_id, M.price, M.cost, M.imei, M.create_time, M.sold, C.cate_id, C.parent_cate_id, C.name
FROM merchandise AS M left join category AS C ON M.cate_id = C.cate_id
WHERE M.me_id = #{me_id} AND M.group_id = #{group_id};

SELECT M.me_id, M.price, M.cost, M.imei, M.create_time, M.sold, C.cate_id, C.parent_cate_id, C.name
FROM merchandise AS M left join category AS C ON M.cate_id = C.cate_id
WHERE M.group_id = #{group_id} AND M.sold = #{sold}
LIMIT #{limit} OFFSET #{offset};

SELECT M.me_id, M.price, M.cost, M.imei, M.create_time, M.sold, C.cate_id, C.parent_cate_id, C.name
FROM merchandise AS M left join category AS C ON M.cate_id = C.cate_id
WHERE M.imei = #{imei} AND M.group_id = #{group_id} AND M.sold = #{sold};

SELECT M.me_id, M.price, M.cost, M.imei, M.create_time, M.sold, C.cate_id, C.parent_cate_id, C.name
FROM merchandise AS M left join category AS C ON M.cate_id = C.cate_id
WHERE M.cate_id = #{cate_id} AND M.group_id = #{group_id} AND M.sold = #{sold};

INSERT INTO merchandise(cate_id, cost, price, imei, create_time, own_id, group_id)
values (#{cate_id}, #{cost}, #{price}, #{imei}, #{create_time}, #{owner_id}, #{group_id})
RETURNING me_id;;


SELECT M.me_id, M.price, M.cost, M.imei, M.create_time, M.sold, C.cate_id, C.parent_cate_id, C.name
FROM merchandise AS M left join category AS C ON M.cate_id = C.cate_id
WHERE C.group_id = #{group_id} AND M.sold = #{sold} AND (imei ILIKE #{ltext} OR C.name ILIKE #{ltext});
