mapper.select:
INSERT INTO orders(me_id, selling_price, remark, selling_time, own_id, group_id)
VALUES (#{me_id}, #{selling_price}, #{remark}, #{selling_time}, #{own_id}, #{group_id})
RETURNING order_id;

SELECT order_id, selling_price, returned, remark, selling_time, M.me_id, cost, price, imei, create_time, sold, C.cate_id, parent_cate_id, name
from orders AS O LEFT JOIN merchandise as M On O.me_id = M.me_id LEFT JOIN category AS C ON M.cate_id = C.cate_id
WHERE M.group_id = #{group_id} AND selling_time >= #{selling_time_start} AND selling_time <= #{selling_time_end};

SELECT order_id, selling_price, returned, remark, selling_time, M.me_id, cost, price, imei, create_time, sold, C.cate_id, parent_cate_id, name
from orders AS O LEFT JOIN merchandise as M On O.me_id = M.me_id LEFT JOIN category AS C ON M.cate_id = C.cate_id
WHERE O.order_id = #{order_id} AND M.group_id = #{group_id};
